def usage():
    print("usage: main.py FLAG DOMAIN\n")
    print("FLAG:\n")
    print("-h, --help\n\tshow this help")
    print("-d, --dnsscan\n\tactiv the dnsscan")
    print("-s, --shodan\n\tactiv the shodan scan")
    print("-t, --theharvester\n\tactiv the thehavester scan")
    print("-u, --urlscan\n\tactiv the urlscan")
    print("DOMAIN:\n")
    print("--domain "+"\x1B[3m"+"\u0332".join("URL")+"\x1B[0m"+"\n\tthe url domain to attack")

def write(file_path, txt):
    with open(file_path, 'w+') as f:
        f.write(txt)

def launch_scans(URL, scans):
    if URL != "":
        global SCAN_PATH
        SCAN_PATH = os.path.join(os.getcwd(),"scans","scan_"+URL)
        # Create directory if not exist
        if not os.path.exists(SCAN_PATH):
            os.makedirs(SCAN_PATH)
            
        # DNSSCAN
        if(scans["DnsScan"]):
            res = DnsScan.scan(URL)
            write(os.path.join(SCAN_PATH, "dnsscan.log"), res)
        # Shodan
        if(scans["Shodan"]):
            res = Shodan.scan(URL)
            write(os.path.join(SCAN_PATH, "shodan.log"), res)
        # TheHarvester
        if(scans["TheHarvester"]):
            res = "Not implemented yet."
            write(os.path.join(SCAN_PATH, "theharvester.log"), res)
        # URLScan
        if(scans["URLScan"]):
            res = URLScan.scan(URL)
            write(os.path.join(SCAN_PATH, "urlscan.log"), res)
    else:
        usage()
        sys.exit(2)

def parseArgs():
    if  "help" in os.environ:
        usage()
        sys.exit(2)
    if "dnsscan" in os.environ:
        SCAN_DICT["DnsScan"] = 1
    if "shodan" in os.environ:
        SCAN_DICT["Shodan"] = 1
    if "theharvester" in os.environ:
        SCAN_DICT["TheHarvester"] = 1
    if "urlscan" in os.environ:
        SCAN_DICT["URLScan"] = 1
    if "domain" in os.environ:
        global URL_DOMAIN
        URL_DOMAIN = os.environ['domain']
    else:
        usage()
        sys.exit(2)
            
    launch_scans(URL_DOMAIN, SCAN_DICT)
            
        
if __name__ == "__main__":
    parseArgs()
